mapper.select:
select r.id,r.name,r.url,r.price,r.jiahu,
lv,xiuwei,equ_xiuwei,sch,pattack_max,mattack_max,pattack_min,mattack_min,
hit,modadd,cri_add_p,cri_sub_p,mdef,pdef,inprotect,avoid,critical,sex,
attackhuman,movespeed,castspeed,lianhu,qinghua,xuansu,guhong,
xiangyun,tinglan,haitang,feihuhuaqiu,tianhulishang,xianhucaijue,canghaisangtian,yeyujiangnan,
huikanfanghu,duncifanghu,huoyuanfanghu,shuifengdufanghu,wanfeng,huxin,
taichu,shilifushou,xiyangyang,mawangye,wanshengtianzun,yehuo,dulanggui,haizi_tiayu,vip9,bihai,changong,changkong,fengyuzihuang
from cbg_role r left join cbg_data d on r.id =d.role_id
where r.yn =1



      and lv = 69


      and lv >= 70 and lv <= 74


      and lv >= 75 and lv <= 79


      and lv >= 80 and lv <= 85


      and lv >= 86 and lv <= 90


      and lv >= 69





    and lv >= #{lvmin}


    and lv <= #{lvmax}



  and sch in

    #{id}



  and jiahu >= #{jiahu}


  and price >= #{pricemin}


  and price <= #{pricemax}



  and mhp >= #{mhp}


  and msp >= #{msp}



  and xiuwei >= #{xiuwei}


  and equ_xiuwei >= #{equXiuwei}



  and pattack_max >= #{pattackMax}


  and mattack_max >= #{mattackMax}


  and pattack_min >= #{pattackMin}


  and mattack_min >= #{mattackMin}


  and hit >= #{hit}


  and modadd >= #{modadd}


  and attadd >= #{attadd}


  and cri_add_p >= #{criAddP}


  and cri_sub_p >= #{criSubP}


  and mdef >= #{mdef}


  and pdef >= #{pdef}



  and pattack_max - pattack_min <= #{xiaogong}


  and mattack_max - mattack_min <= #{xiaofa}



  and inprotect >= #{inprotect}


  and critical >= #{critical}


  and avoid >= #{avoid}


  and attdef >= #{attdef}


  and defhuman >= #{defhuman}


  and attackhuman >= #{attackhuman}



  and inprotect >= #{inprotect}


  and sract >= #{sract}


  and srbody >= #{srbody}


  and srmind >= #{srmind}


  and movespeed >= #{movespeed}


  and castspeed >= #{castspeed}


  and attackspeed >= #{attackspeed}


  and thump_add_p >= #{thumpAddP}


  and thump_sub_p >= #{thumpSubP}


  and strong >= #{strong}


  and body >= #{body}


  and quick >= #{quick}


  and dodge >= #{dodge}


  and soul >= #{soul}


  and mind >= #{mind}


  and lianhu >= #{lianhu}


  and haizi_lv >= #{haiziLv}


  and haizi_zizhi >= #{haiziZizhi}


  and haizi_wuxue >= #{haiziWuxue}



  and awake_lv >= #{awakeLv}


  and release_lv >= #{releaseLv}


  and awake_value >= #{awakeValue}


  and awake_lv >= #{awakeLv}


  and release_lv >= #{releaseLv}


  and awake_value >= #{awakeValue}


  and minglian >= #{minglian}



  and qinghua =1


  and xuansu =1


  and guhong =1


  and xiangyun =1


  and tinglan =1


  and haitang =1


  and feihuhuaqiu =1


  and tianhulishang =1


  and xianhucaijue =1


  and canghaisangtian =1


  and yeyujiangnan =1


  and huikanfanghu =1


  and duncifanghu =1


  and huoyuanfanghu =1


  and shuifengdufanghu =1




  and wanfeng =1


  and huxin =1


  and taichu =1


  and shilifushou =1


  and xiyangyang =1


  and mawangye =1


  and wanshengtianzun =1


  and yehuo =1


  and dulanggui =1


  and lignt_menpai =1


  and haizi_tiayu =1


  and vip9 =1



  and bihai =1


  and changkong =1


  and changong =1


  and fengyuzihuang =1
